div.script:
function setBullet(id)
{
     var elem = document.getElementById(id);
     if (elem.getAttribute("src") == "/img/transparentdot.png")
     {
         elem.setAttribute("src", "/img/reddot.png");
     }
     else
     {
         elem.setAttribute("src", "/img/transparentdot.png");
     }
}
active_line = "";
