div.script:
//
$('#terminal_container').height($(document).height());
terminal = $('#terminal_container').terminal('shell', {custom_prompt : "", hello_message : 'Click \'run\' to execute the program above.', inherit_style : 'true'});

function reload_terminal()
{
    var data = new FormData();
	data.append("session_id", session_id);
	data.append("input", "");
	var request = new XMLHttpRequest();
	request.onreadystatechange = function()
	{
		if (this.readyState == 4 && this.status == 200)
		{
            if (this.responseText != "")
            {
                global_terminal_append(this.responseText);
            }
		}
	};
	request.open("POST", "shell", true);
	request.timeout = 2000;
	request.send(data);
}
